import styles from "./Project.module.css"
import { useParams } from "react-router-dom"
import { useState, useEffect } from "react"
import Loading from "../layout/Loading"
import Container from "../layout/Container"


function Project(){

    const {id} = useParams()
    const [project, setProject]= useState([])
    const [showProjectForm, setshowProjectForm]= useState([])

    useEffect(()=>{
        setTimeout(()=>{
            fetch(`http://localhost:5000/projects/${id}`,{
                method:"GET",   
                headers:{"Content-Type": "application/json",},
                })
                .then((resp)=> resp.json())
                .then((data)=>{
                    setProject(data)
                    console.log(data)

                })
                .catch((err)=>console.log(err))

        },3000)


    },[id])

    const toggleProjectForm=()=>{
        setshowProjectForm(!showProjectForm)
    }

    return(
        <>
        {project.name ? (
            <div>
                <Container >
                    <div>
                        <h1>Projeto: {project.name}</h1>
                        <button onClick={toggleProjectForm}>{!showProjectForm ? "Editar projeto" : "Fechar" }</button>

                        {!showProjectForm ? (
                            <div>
                                <p><span>categoria: {project.category.name}</span></p>
                                <p><span>Total do Orçamento: {project.budget}</span></p>
                                <p><span>Total utilizado: R${}</span></p>
                            </div>
                        ) : (
                            <div>detalhes do Projeto</div>
                        )}
                    </div>


                </Container>
            </div>
        ) :( <Loading/>)}
        </>
    )
}


export default Project
