        fetch(`http://localhost:5000/projects/${id}`,{
        method:"GET",   
        headers:{"Content-Type": "application/json",},
        })
        .then((resp)=> resp.json())
        .then((data)=>{
            setProject(data)

        })
        .catch((err)=>console.log(err))


    },[id])
 console.log(project.name)
